import React, { useState } from "react";
import { ComponentTypes, componentSchemas } from "../schemas/componentSchemas";

export function NodeBuilder({ onAdd, parentId = null }) {
  const [selectedType, setSelectedType] = useState(ComponentTypes.Table);
  const [nodeProperties, setNodeProperties] = useState({});

  const handleAdd = () => {
    const schema = componentSchemas[selectedType];
    const newNode = {
      ...Object.keys(schema).reduce((acc, key) => {
        if (typeof schema[key] === "object" && schema[key] !== null) {
          acc[key] =
            typeof schema[key].default === "function"
              ? schema[key].default()
              : schema[key].default;
        } else {
          acc[key] = schema[key];
        }
        return acc;
      }, {}),
      ...nodeProperties, // Apply user-defined properties
    };
    onAdd(newNode, parentId);
  };

  const handlePropertyChange = (e) => {
    setNodeProperties({
      ...nodeProperties,
      [e.target.name]: e.target.value,
    });
  };

  return (
    <div>
      <select
        value={selectedType}
        onChange={(e) => setSelectedType(e.target.value)}
      >
        {Object.values(ComponentTypes).map((type) => (
          <option key={type} value={type}>
            {type}
          </option>
        ))}
      </select>
      <div>
        {/* Assuming you have fields to modify the properties */}
        <input
          type="text"
          name="text"
          placeholder="Text"
          onChange={handlePropertyChange}
        />
        {/* Add more fields as required by your schema */}
      </div>
      <button onClick={handleAdd}>Add Node</button>
    </div>
  );
}
